package com.example.practica1

import android.media.Image
import android.os.Bundle
import com.google.android.material.appbar.CollapsingToolbarLayout
import com.google.android.material.floatingactionbutton.FloatingActionButton
import com.google.android.material.snackbar.Snackbar
import androidx.appcompat.app.AppCompatActivity
import android.view.Menu
import android.view.MenuItem
import android.widget.ImageView
import com.bumptech.glide.Glide
import com.bumptech.glide.load.engine.DiskCacheStrategy
import com.example.practica1.databinding.ActivityScrollingBinding

class ScrollingActivity : AppCompatActivity() {

    private lateinit var binding: ActivityScrollingBinding

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)

        binding = ActivityScrollingBinding.inflate(layoutInflater)
        setContentView(binding.root)

        Glide.with(this)
            .load("https://e00-elmundo.uecdn.es/assets/multimedia/imagenes/2017/10/16/15081525278135.jpg")
            .diskCacheStrategy(DiskCacheStrategy.AUTOMATIC)
            .into(findViewById(R.id.img_pepe))

        Glide.with(this)
            .load("https://static.wikia.nocookie.net/doblaje/images/5/5b/Jesucristo-0.jpg/revision/latest/top-crop/width/360/height/450?cb=20190810015342&path-prefix=es")
            .diskCacheStrategy(DiskCacheStrategy.AUTOMATIC)
            .into(findViewById(R.id.img_jesus))

        Glide.with(this)
            .load("data:image/jpeg;base64,")
            .diskCacheStrategy(DiskCacheStrategy.AUTOMATIC)
            .into(findViewById(R.id.img_soria))
    }

    override fun onCreateOptionsMenu(menu: Menu): Boolean {
        // Inflate the menu; this adds items to the action bar if it is present.
        menuInflater.inflate(R.menu.menu_scrolling, menu)
        return true
    }

    override fun onOptionsItemSelected(item: MenuItem): Boolean {
        // Handle action bar item clicks here. The action bar will
        // automatically handle clicks on the Home/Up button, so long
        // as you specify a parent activity in AndroidManifest.xml.

        return when (item.itemId) {
            R.id.action_settings -> true
            else -> super.onOptionsItemSelected(item)
        }
    }
}